// import { BrowserRouter, Route } from "react-router-dom";
import { useState } from "react";
import Pregunta from "./Pregunta";

const Preguntas = ({ global, setGlobal, cambioVal }) => {
	// ! YA FUNCIONALA LISTAAAA !!! AHORA FALTA EL ACUMULADOR
	const puntuacion = global.reduce((acc, el) => {
		if (el.estado === true) {
			return (acc = acc + 1);
		} else {
			console.log(acc);
			return acc;
		}
	}, 0);

	// console.log(global);

	if (global[0].estado === false) {
		return (
			<Pregunta cambioVal={cambioVal} setGlobal={setGlobal} global={global} posicion={1}></Pregunta>
		);
	} else if (global[1].estado === false) {
		return (
			<Pregunta cambioVal={cambioVal} setGlobal={setGlobal} global={global} posicion={2}></Pregunta>
		);
	} else if (global[2].estado === false) {
		return (
			<Pregunta cambioVal={cambioVal} setGlobal={setGlobal} global={global} posicion={3}></Pregunta>
		);
	} else if (global[3].estado === false) {
		return (
			<Pregunta cambioVal={cambioVal} setGlobal={setGlobal} global={global} posicion={4}></Pregunta>
		);
	} else {
		return (
			<div>
				<h1>Felicidades!! Acabaste la prueba</h1>
				<p>
					Tu puntuacion es <span>{puntuacion}</span>
				</p>
			</div>
		);
	}
};

export default Preguntas;
