import { useState } from "react";
import { Link, BrowserRouter, Route } from "react-router-dom";
import Preguntas from "./components/Preguntas";
function App() {
	const [global, setGlobal] = useState([{ estado: false }, { estado: false }]);
	// const changeEstadoApp = () => {
	//Aqi iria la forma de como cambiar el estado global y tendriamos que pasar ala siguiente rpegutna
	// };

	//* LO QUE TENEMOS QUE HACER ES CREAR UN TIPO DE ESTAOD EN APP EL CUAL CONTROLE SI TIENE ALGUN VALOR (POR DEFECTO SERIA "FALSE") DE ESA FORMA CONTROLAMOS SI EXISTE UNA RESPUESTA O NO, SI NO EXISTIERA  SE ENVIA UN MENSAJE ,SI existe un respuesta(no importa si es buena o malo) el componente cambiaría por la siguiente pregunta y asi sucesivamente.
	const cambioVal = () => {
		const object = { estado: true };
		setGlobal({ ...global, [[0]]: object });
	};
	console.log(global);
	return (
		<div>
			<BrowserRouter>
				<Route exact path="/">
					<div>
						<h1>Bievenido a Preguntados</h1>
						<Link to="/Preguntas">Ir a las preguntas</Link>
						<button onClick={cambioVal}>Presiona</button>
					</div>
				</Route>
				<Route exact path="/Preguntas" component={Preguntas}>
					<Preguntas global={global} setGlobal={setGlobal}></Preguntas>
				</Route>
			</BrowserRouter>
		</div>
	);
}

export default App;
