	}

	// Mostrando la siguiente pregunta si el estado global su propiedad estado es falso
	if (global[0].estado === false) {
		return (
			<Pregunta1
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={1}
				respuestas={respuestas}
				setRespuestas={setRespuestas}
			></Pregunta1>
		);
	} else if (global[1].estado === false) {
		return (
			<Pregunta2
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={2}
				respuestas={respuestas}
				setRespuestas={setRespuestas}
			></Pregunta2>
		);
	} else if (global[2].estado === false) {
		return (
			<Pregunta3
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={3}
				respuestas={respuestas}
				setRespuestas={setRespuestas}
			></Pregunta3>
		);
	} else if (global[3].estado === false) {
		return (
			<Pregunta4
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={4}
				respuestas={respuestas}
				setRespuestas={setRespuestas}
			></Pregunta4>
		);
	} else {
		return (
			<div>
				<h1>Felicidades!! Acabaste la prueba</h1>
				<p>
					Tu puntuacion es <span>{puntuacion}</span>
				</p>
				<button style={butonStyle}>
					<Link to="/" style={aStyle}>
						Empezar denuevo
					</Link>
				</button>
			</div>
		);
	}
};

export default Preguntas;
