import { useState } from "react";
import data from "../Data";
const Pregunta = ({ posicion }) => {
	const [preguntas] = useState([data]);
	const [questions] = preguntas;
	// console.log(questions[0]);
	let number = posicion - 1;
	return (
		<div>
			<p>
				<span>{questions[number].preguntas}</span>
			</p>
			<form>
				<input type="radio" id="respuesta" name="pregunta1" value="2020" />
				<label>{questions[number].opciones[0]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2021" />
				<label>{questions[number].opciones[1]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2022" />
				<label>{questions[number].opciones[2]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2022" />
				<label>{questions[number].opciones[3]}</label>
			<button > </button>
			</form>
		</div>
	);
};
export default Pregunta;
