import { useState } from "react";
import { Link, BrowserRouter, Route } from "react-router-dom";
import Preguntas from "./components/Preguntas";

const butonSyle = {
	backgroundColor: "blue",
	padding: "5px 9px ",
	color: "white",
};
const linkStyle = {
	textDecoration: "none",
};
function App() {
	const [global, setGlobal] = useState([
		{ id: 0, estado: false },
		{ id: 1, estado: false },
		{ id: 2, estado: false },
		{ id: 3, estado: false },
	]);

	const cambioVal = i => {
		const object = { id: i, estado: true };
		console.log(object);
		const newGlobla = global.map(elemento => {
			if (elemento.id === object.id) {
				return {
					...elemento,
					estado: true,
				};
			}
			return elemento;
		});

		setGlobal(newGlobla);
	};

	// console.log(global[0]);
	return (
		<div>
			<BrowserRouter>
				<Route exact path="/">
					<div>
						<h1>Bievenido a Preguntados</h1>
						<button style={butonSyle}>
							<Link style={linkStyle} to="/Preguntas">
								Ir a las preguntas
							</Link>
						</button>
					</div>
				</Route>
				<Route exact path="/Preguntas" component={Preguntas}>
					<Preguntas cambioVal={cambioVal} global={global} setGlobal={setGlobal}></Preguntas>
				</Route>
			</BrowserRouter>
		</div>
	);
}

export default App;
