import { useState } from "react";
import data from "../Data";
const Pregunta1 = ({ posicion, cambioVal, respuestas, setRespuestas }) => {
	const [preguntas] = useState([data]);
	const [questions] = preguntas;
	let num = posicion - 1;
	const getAnswerForm = e => {
		console.log(respuestas[num]);
		if (
			e.target.value === "USA" ||
			e.target.value === "282" ||
			e.target.value === "1938" ||
			e.target.value === "ballena azul"
		) {
			const newRespuestas = respuestas.map(ele => {
				if (e.target.value === ele.rpt) {
					return { ...ele, valor: true };
				} else return ele;
			});
			setRespuestas(newRespuestas);
		}
		else {} // console.log("Respuesta Incorrecta");
	};

	console.log(respuestas);
	const selectAnswer = e => {
		e.preventDefault();
		cambioVal(num);
	};

	return (
		<div>
			<p>
				<span>{questions[num].preguntas}</span>
			</p>
			<form onSubmit={selectAnswer} onChange={getAnswerForm}>
				<input
					type="radio"
					id="respuesta1"
					name="respuestas"
					value={questions[num].opciones[0]}
				/>
				<label>{questions[num].opciones[0]}</label>
				<input
					type="radio"
					id="respuesta2"
					name="respuestas"
					value={questions[num].opciones[1]}
				/>
				<label>{questions[num].opciones[1]}</label>
				<input
					type="radio"
					id="respuesta3"
					name="respuestas"
					value={questions[num].opciones[2]}
				/>
				<label>{questions[num].opciones[2]}</label>
				<input
					type="radio"
					id="respuesta4"
					name="respuestas"
					value={questions[num].opciones[3]}
				/>
				<label>{questions[num].opciones[3]}</label>
				<button>Enviar</button>
			</form>
		</div>
	);
};
export default Pregunta1;
