import { useState } from "react";
import data from "../Data";
const Pregunta = ({ posicion, cambioVal }) => {
	const [preguntas] = useState([data]);
	const [respuestas, setRespuestas] = useState({ valor1: false });
	const [questions] = preguntas;
	let num = posicion - 1;

	// console.log(e.target.type);
	// console.log(e.target.checked);
	// console.log(e.target.value);
	// Si la respuesta es usa tendriamos que cambiar el estado de valor1 a "true",despues hacemos un reduce de este arreglo y sumamos los valores que sea true así tendriamos su respuesta.
	const getAnswerForm = e => {
		// if (e.target.value === "USA") {
		// console.log(respuestas[num]);
		// console.log("Respuesta correcta");
		// } else console.log("Respuesta INcorrecta");
		console.log(e.target.value);
	};

	// console.log(respuestas);
	const selectAnswer = e => {
		e.preventDefault();
		cambioVal(num);
		// console.log(posicion);
	};

	return (
		<div>
			<p>
				<span>{questions[num].preguntas}</span>
			</p>
			<form onSubmit={selectAnswer} onChange={getAnswerForm}>
				<input
					type="radio"
					id="respuesta"
					name="respuestas"
					value={questions[num].opciones[0]}
				/>
				<label>{questions[num].opciones[0]}</label>
				<input
					type="radio"
					id="respuesta"
					name="respuestas"
					value={questions[num].opciones[1]}
				/>
				<label>{questions[num].opciones[1]}</label>
				<input
					type="radio"
					id="respuesta"
					name="respuestas"
					value={questions[num].opciones[2]}
				/>
				<label>{questions[num].opciones[2]}</label>
				<input
					type="radio"
					id="respuesta"
					name="respuestas"
					value={questions[num].opciones[3]}
				/>
				<label>{questions[num].opciones[3]}</label>
				<button>Enviar</button>
			</form>
		</div>
	);
};
export default Pregunta;
