import { useState } from "react";
import { Link, BrowserRouter, Route } from "react-router-dom";
import Preguntas from "./components/Preguntas";
// Estilos Css
const main_container = {
	display: "flex",
	flexDirection: "column",
	flexWrap: " nowrap",
	alignContent: " center",
	justifyContent: "center",
	alignItems: "center",
};
const title = {

	color: "white",
	fontSize: "10em",
	fontWeight: "bolder",
	textDecoration: "underline",
	// textShadow: "2px solid black",
	textShadow: " 1px 1px 3px black",
};
const butonSyle = {
	color: "black",
	fontSize: "1.4em",
	maxWidth: "300px",
	backgroundColor: "#985bda",
	padding: "15px 8px ",
	border: "none",
	borderRadius: "8px",
};
const linkStyle = {
	color: "#eee",
	textDecoration: "none",
};

function App() {
	// Estado para saber si existe una respuesta
	const [global, setGlobal] = useState([
		{ id: 0, estado: false },
		{ id: 1, estado: false },
		{ id: 2, estado: false },
		{ id: 3, estado: false },
	]);

	// Metodo para cambiar el valor de estado de false a true
	// Este metodo se ejucta en Pregunta.js
	// Esto lo que hace es que cada vez que seleccionemos siguiente va cambiar el estado por verdadero Ocasionando que se renderize un nuevo componente (osea el de la siguiente pregunta)
	const cambioVal = i => {
		const object = { id: i, estado: true };
		const newGlobal = global.map(elemento => {
			if (elemento.id === object.id) {
				return {
					...elemento,
					estado: true,
				};
			}
			return elemento;
		});
		setGlobal(newGlobal);
	};

	return (
		<div>
			<BrowserRouter>
				<Route exact path="/">
					<div style={main_container}>
						<h1 style={title}>Kahoot!</h1>
						<button style={butonSyle}>
							<Link style={linkStyle} to="/Preguntas">
								Ir a las preguntas
							</Link>
						</button>
					</div>
				</Route>
				<Route exact path="/Preguntas" component={Preguntas}>
					<Preguntas cambioVal={cambioVal} global={global} setGlobal={setGlobal}></Preguntas>
				</Route>
			</BrowserRouter>
		</div>
	);
}

export default App;
