import { Link, BrowserRouter, Route } from "react-router-dom";
import Preguntas from "./components/Preguntas";
function App() {
	const [global, setGlobal] = useState([
		"",
		"",
		"",
		"",
		"", // { estado: false },
		// { estado: false },
		// { estado: false },
		// { estado: false },
	]);

	const cambioVal = i => {
		const object = { id: "0", estado: true };
		// setGlobal(global.concat({ [i]: object }));
		setGlobal([...global, object]);

		// ! PROBLEMA !! LO QUE SUCEDE ES QUE CUANDO MODIFICAMOS EL ESTADO ,SE NOS CREAR UN OBJETO AL FINAL Y ESTO OCASIONA QUE NUESTRO ESTADO GLOBAL SE CONVIERTE EN UN "OBJETO" Y POR ESO NOS SALE EL ERROR DE QUE NO ES UN METODO YA QUE (REDUCE O FOREAHC SON METODOS DE ARREGLO Y NO DE OBJETOS)
	};

	console.log(global[0]);
	return (
		<div>
			<BrowserRouter>
				<Route exact path="/">
					<div>
						<h1>Bievenido a Preguntados</h1>
						<Link to="/Preguntas">Ir a las preguntas</Link>
					</div>
				</Route>
				<Route exact path="/Preguntas" component={Preguntas}>
					<Preguntas cambioVal={cambioVal} global={global} setGlobal={setGlobal}></Preguntas>
				</Route>
			</BrowserRouter>
		</div>
	);
}

export default App;
