import { useState } from "react";
import data from "../Data";
const Pregunta = ({ posicion, cambios, global, setGlobal }) => {
	const [preguntas] = useState([data]);
	// const [estado, setEstado] = useState(false);
	const [questions] = preguntas;
	let num = posicion - 1;
	// posicion = 0;
	// console.log(questions[0]);

	// const sendAnswer = e => {
	// 	e.preventDefault();
	// 	setEstado(!estado);
	// };

	// console.log("Soy posicion de pregunta", posicion);
	// console.log(num);
	console.log("Soy global desde Pregunta", global[0]);
	const selectAnswer = e => {
		e.preventDefault();
		setGlobal({ [global[0].estado]: true });
	};

	return (
		<div>
			<p>
				<span>{questions[num].preguntas}</span>
			</p>
			{/* <form> */}
			<form onSubmit={selectAnswer}>
				<input type="radio" id="respuesta" name="pregunta1" value="2020" />
				<label>{questions[num].opciones[0]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2021" />
				<label>{questions[num].opciones[1]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2022" />
				<label>{questions[num].opciones[2]}</label>
				<input type="radio" id="respuesta" name="pregunta1" value="2022" />
				<label>{questions[num].opciones[3]}</label>
				<button>Enviar </button>
			</form>
		</div>
	);
};
export default Pregunta;
