import Pregunta from "./Pregunta";
import { useState } from "react";
const Preguntas = ({ global, setGlobal, cambioVal }) => {
	const [respuestas, setRespuestas] = useState([
		{ rpt: "USA", valor: false },
		{ rpt: "1938", valor: false },
		{ rpt: "282", valor: false },
		{ rpt: "ballena azul", valor: false },
	]);

	const puntuacion = global.reduce((acc, el) => {
		if (el.estado === true) {
			return (acc = acc + 1);
		} else {
			// console.log(acc);
			return acc;
		}
	}, 0);

	if (global[0].estado === false) {
		return (
			<Pregunta
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={1}
			></Pregunta>
		);
	} else if (global[1].estado === false) {
		return (
			<Pregunta
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={2}
			></Pregunta>
		);
	} else if (global[2].estado === false) {
		return (
			<Pregunta
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={3}
			></Pregunta>
		);
	} else if (global[3].estado === false) {
		return (
			<Pregunta
				cambioVal={cambioVal}
				setGlobal={setGlobal}
				global={global}
				posicion={4}
			></Pregunta>
		);
	} else {
		return (
			<div>
				<h1>Felicidades!! Acabaste la prueba</h1>
				<p>
					Tu puntuacion es <span>{puntuacion}</span>
				</p>
			</div>
		);
	}
};

export default Preguntas;
